import { Pool } from 'pg';

class Database{
    constructor(){
        this.pool = new Pool({
            host: 'localhost',
            user: 'postgres',
            database: 'learn',
            password: 'password',
            port: 5432
        });

        this.connect = async () => this.pool.connect(()=> console.log('waooo!, pg connected'));

        this.users = `
        CREATE TABLE IF NOT EXISTS users(
        id serial PRIMARY KEY,
        firstName VARCHAR (50) NOT NULL,
        lastName VARCHAR (50) NOT NULL,
        email VARCHAR (100) NOT NULL,
        password VARCHAR(255) NOT NULL,
        isAdmin BOOLEAN NOT NULL DEFAULT false,
        userRole VARCHAR (10) NOT NULL DEFAULT 'user',
        createdOn TIMESTAMP NOT NULL
        )
        `;
         this.post = `
        CREATE TABLE IF NOT EXISTS sessions(
            id serial PRIMARY KEY,
            auther VARCHAR(100) NOT NULL,
            post VARCHAR (2000) NOT NULL,
            createdOn TIMESTAMP NOT NULL,
            FOREIGN KEY (auther) REFERENCES users(id) ON DELETE CASCADE ON UPDATE CASCADE
        )
        `;
        this.comment = `
        CREATE TABLE IF NOT EXISTS reviews(
            id serial PRIMARY KEY,
            sender VARCHAR(50) NOT NULL,
            post VARCHAR(20) NOT NULL,
            comment VARCHAR (255) NOT NULL,
            createdOn TIMESTAMP NOT NULL,
            FOREIGN KEY (snder) REFERENCES users(id) ON DELETE CASCADE ON UPDATE CASCADE)
            FOREIGN KEY (post) REFERENCES post(id) ON DELETE CASCADE ON UPDATE CASCADE
        `;

        this.initializeDb(); 
    }

    async executeQuery(query, data=[]) {
        const connection = await this.connect();
        try {
          // execute a query with parameter
          if (data.length) {
            return await connection.query(query, data);
          }
          // execute a query without parameter
          return await connection.query(query);

        } catch (error) {
          return error;
        } 
        // finally {
        //   connection.release();
        // }

        }

      async initializeDb() {
        await this.executeQuery(this.users);
        await this.pool.end()
        await this.executeQuery(this.post);
        await this.pool.end()
        await this.executeQuery(this.comment);
        await this.pool.end()
      }
}

export default new Database();

